def chuli(fname, store_num):
    df = pd.read_excel(fname, skiprows=7,engine = 'openpyxl')
    df = df.iloc[:, 1:]
    df.columns = lst
    df.columns = lst2
    if store_num == '001库':
        df = df.loc[df.store == '001']
    elif store_num == '002电商库':
        df = df.loc[df.store == '002']
    else:
        df = df.loc[(df.store == '001') | (df.store == '002')]

    df['content'] = df['end_ben'] / df['end_jian']
    df = df[df['store'] != '制表人:']
    df = df[df['store'] != '合计：']
    df = df[df['store'].notnull()]
    df = df.iloc[:, 2:]
    df = df[lst3]
    df['bianma01'] = df['class02'].map(dic_class)
    df['bianma02'] = df['class05'].map(dic_class)
    df = df.sort_values(['bianma01','bianma02'])
    df = df.iloc[:,:-2]
    return df

def danjuChuli(df,store_num):
    if store_num == '001':
        df = df.loc[df['仓库编码'] == '001']
    elif store_num == '002':
        df = df.loc[df['仓库编码'] == '002']
    else:
        df = df.loc[(df['仓库编码'] == '001') | (df['仓库编码'] == '002')]

    # return df
    df = df.dropna(how='all', axis=0)
    cols = [i for i in df.columns.to_list() if 'Unnamed' not in i]
    df = df[cols]
    # get maxrows
    for label, ser in df.items():
        for num0, x in enumerate(ser):
            if isinstance(x, str):
                if '合计' in x:
                    index = num0
                    # print(index)
                    break
    df = df.iloc[:index]
    df = df.dropna(how='all', axis=1)
    return df

def addBen(string):
    pattern = r'(?P<num>\d+)本/件'
    regexp1 = re.compile(pattern)
    mat = regexp1.search(string)
    if mat:
        content = int(mat.group('num'))
    else :
        content  = 0
    return content

def diaobo(fname):
    df_diaobo0 = pd.read_excel(fname, skiprows=6)
    df_diaobo = df_diaobo0[['单据日期','创建时间','单据编号','调出仓库编码','调入仓库编码','存货编码','数量','数量2']]
    df_diaobo = df_diaobo[~df_diaobo['单据编号'].isna()]
    df_diaobo1 = df_diaobo[(df_diaobo['调出仓库编码'] == '002') & (df_diaobo['调入仓库编码'] == '001')]
    df_diaobo1['数量'] = df_diaobo1['数量']*-1
    df_diaobo1['数量2'] = df_diaobo1['数量2']*-1
    pivot = pd.pivot_table(df_diaobo1,index = '存货编码',aggfunc = "sum",margins = False)
    pivot = pivot.reset_index()
    pivot = pivot[['存货编码','数量','数量2']]
    return df_diaobo1,pivot
def dfAdddiaobo(df):
    for i in ['begin_ben',
       'begin_jian', 'ruku_ben', 'ruku_jian', 'chuku_ben', 'chuku_jian',
       'end_ben', 'end_jian','数量','数量2']:
        df[i] = df[i].fillna(0)
    df.ruku_ben = df.ruku_ben + df['数量']
    df.ruku_jian = df.ruku_jian + df['数量2']
    df.chuku_ben = df.chuku_ben + df['数量']
    df.chuku_jian = df.chuku_jian + df['数量2']
    df = df.iloc[:,:-3]
    return df


